package com.creditease.ns4.gear.watchdog.common.log;import com.creditease.ns.log.NsLog;/** * @author 杨红岩 * @description NsLogger * @date 2019/2/27 */public class NsLogger {    /**     * 日志类型     */    public enum LoggerType {        /**         * watchdog 日志         */        LOG_WATCHDOG("nsg.watchdog"),        /**         * watchdog plugin 日志         */        LOG_WATCHDOG_PLUGIN("nsg.watchdog.plugin"),        /**         * watchdog collect 日志         */        LOG_WATCHDOG_COLLECT("nsg.watchdog.collect");        private String value;        LoggerType(String value) {            this.value = value;        }        public String getValue() {            return this.value;        }    }    public static NsLog getWatchdogLogger() {        return getLogger(LoggerType.LOG_WATCHDOG);    }    public static NsLog getWatchdogPluginLogger() {        return getLogger(LoggerType.LOG_WATCHDOG_PLUGIN);    }    public static NsLog getWatchdogCollectLogger() {        return getLogger(LoggerType.LOG_WATCHDOG_COLLECT);    }    public static NsLog getLogger(LoggerType key) {        return NsLog.getLog(key.getValue());    }}