package com.creditease.ns4.gear.watchdog.monitor.process.constant;import com.creditease.ns4.gear.watchdog.common.constant.ServerVendor;import java.util.HashMap;import java.util.Map;/** * @author 杨红岩 * @description 进程常量类 * @date 2019/1/16 */public class PROCES {    /**     * 启动状态枚举     */    public enum STATUS {        /**         * 停止 -> -1         */        STOP(-1, "停止"),        /**         * 启动中 -> 0         */        STARTING(0, "启动中"),        /**         * 启动成功 -> 1         */        SUCCESS(1, "启动成功");        private int code;        private String value;        STATUS(int code, String value) {            this.code = code;            this.value = value;        }        public int getCode() {            return this.code;        }        public String getValue() {            return this.value;        }    }    /**     * app默认名称     */    public final static String APP_NAME_DEFAULT = "default-app";    /**     * APP启动入口 -> Bootstrap     */    public final static String APP_MAIN_NSDISPATCHER = "com.creditease.ns.dispatcher.core.Bootstrap";    /**     * APP启动入口 -> XmlAppTransporterContext     */    public final static String APP_MAIN_NSTRANSPORTER = "com.creditease.ns.transporter.context.XmlAppTransporterContext";    /**     * APP启动入口类集合     */    public final static Map<String, String> APP_MAIN = new HashMap<String, String>(2) {        private static final long serialVersionUID = -8094495193944863172L;        {            /*NS4 启动类 -> NSDispatcher*/            this.put(ServerVendor.NSDISPATCHER.toString(), APP_MAIN_NSDISPATCHER);            /*NS4 启动类 -> NSTransporter*/            this.put(ServerVendor.NSTRANSPORTER.toString(), APP_MAIN_NSTRANSPORTER);        }    };    /**     * 操作     */    public enum Options {        /**         * 启动 -> start         */        START,        /**         * 停止 -> stop         */        STOP,        /**         * 重启 -> restart         */        RESTART    }}