package com.creditease.ns4.gear.watchdog.process;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.lang.management.ManagementFactory;import java.lang.management.MemoryMXBean;import java.lang.management.MemoryUsage;/** * @author 杨红岩 * @description 获取进程id * @date 2019/1/21 */public class ProcessID {    public static void main(String[] args) {        MemoryMXBean bean = ManagementFactory.getMemoryMXBean();        MemoryUsage memoryUsage = bean.getHeapMemoryUsage();        System.out.println(memoryUsage.getUsed());    }}