package com.creditease.ns4.gear.watchdog.shutdown;import java.util.Timer;import java.util.TimerTask;/** * @author 杨红岩 * @description 关闭进程测试 * @date 2019/2/28 */public class Shutdown {    public static boolean flag = false;    public static void main(String[] args) {        Timer timer = new Timer(true);        TimerTask task = new TimerTask() {            public void run() {                System.out.println("aaa");            }        };        timer.schedule(task, 1000 * 30);        try {            Thread.sleep(2000 * 30);        } catch (InterruptedException e) {            e.printStackTrace();        }    }}